import { EthProvider } from "./contexts/EthContext";
//import Intro from "./components/Intro/";
//import Setup from "./components/Setup";
//import Demo from "./components/Demo";
//import Footer from "./components/Footer";
import SimpleMission from "./components/SimpleMission";
import ValidateMission from "./components/ValidateMission";
import React, {useState, useEffect } from 'react';

import {
  BrowserRouter as Router,
  Routes,
  Route,
  NavLink,
} from "react-router-dom";

function App() {

  const [missions, setMissions] = useState([]);

  const handleCreateMission = (mission) => {
    setMissions([...missions, mission]);
  };


  return (


    <EthProvider>
      <Router>
        <nav className="navbar navbar-expand-lg navbar-light bg-light">
          <button className="navbar-toggler" type="button" data-bs-toggle="collapse" data-bs-target="#navbarNavAltMarkup" aria-controls="navbarNavAltMarkup" aria-expanded="false" aria-label="Toggle navigation">
            <span className="navbar-toggler-icon"></span>
          </button>
          <div className="collapse navbar-collapse" id="navbarNavAltMarkup">
            <div className="navbar-nav container">

              <NavLink
                to="/"
                className={({ isActive }) => isActive ? 'nav-item nav-link active' : 'nav-item nav-link'}>
                Create mission
              </NavLink>

              <NavLink
                to="/simple-greeter"
                className={({ isActive }) => isActive ? 'nav-item nav-link active' : 'nav-item nav-link '}>
                Validate mission
              </NavLink>
            </div>
          </div>
        </nav>
        <div className="container pt-3">
          <Routes>
            <Route exact path="/" element={<SimpleMission />} />
            <Route path="/" element={<ValidateMission />} />

          </Routes>
        </div>

        <div>
          <h1>My Mission Tracker</h1>
          <CreateMission onCreateMission={handleCreateMission} />
          <ValidateMission missions={missions} />
        </div>
      </Router>
    </EthProvider>
  );
}

export default App;

//Quickfix for terminal use, run in the project: export NODE_OPTIONS=--openssl-legacy-provider
