import { useEffect, useState } from 'react'
import Web3 from 'web3'


const web3 = new Web3(Web3.givenProvider || "http://localhost:7545");



export default function GenerateToken() {



  const [account, setAccount] = useState('')
  const [balance, setBalance] = useState(0)
  const [network, setNetwork] = useState(0)
  const [contract, setContract] = useState()
  const [totalHours, setTotalHours] = useState(0)
  const [allHours, setAllHours] = useState([])
  const [name, setName] = useState('');
  const [message, setMessage] = useState('');
  const [hours, setHours] = useState(0);

  useEffect(() => {

    async function loadAccountAndContract() {
        // We load the account being connected
        const accounts = await web3.eth.requestAccounts()
        const _account = accounts[0]
        setAccount(_account)

        // We get the network type
        const network = await web3.eth.net.getNetworkType()
        setNetwork(network)

        // We load the balance for the account and format the balance
        const balance = await web3.eth.getBalance(_account)
        setBalance((balance / 1e18).toFixed(4))

        // We connect to the contact
        // const networkID = await web3.eth.net.getId();
        // const { abi } = artifact;
        // try {
        //     const address = artifact.networks[networkID].address
        //     const contract = new web3.eth.Contract(abi, address);
        //     setContract(contract)
        // } catch (err) {
        //     console.error(err);
        // }
    }

    loadAccountAndContract()

    }, [])

    useEffect(() => {
      if (contract) {
          getTotalHours()
          getAllHours()
      }
  }, [contract])

  const getTotalHours = async () => {
      const totalHours = await contract.methods.getTotalHours().call()
      setTotalHours(totalHours)
  }

  const getAllHours = async () => {
      const allHours = await contract.methods.getAllHours().call()
      console.log(allHours);
      setAllHours(allHours)
  }

  const handleSubmit = async (event) => {

      // event.preventDefault()


      try {
          const transaction = await contract
              .methods
              .buyHours(
                  message.trim(),
                  name.trim(),
                  Web3.utils.toWei('0.001', 'ether')
              )
              .send({ from: account })

          console.log("Mining...", transaction);

      } catch (e) {

      }
  }

  const handleOnNameChange = (event) => {
    setName(event.target.value)
  }

  const handleOnMessageChange = (event) => {
    setMessage(event.target.value)
  }

  const handleNameChange = (event) => {
    setName(event.target.value)
  }

  const handleHoursChange = (event) => {
    setHours(event.target.value)
  }

  const handleAccountChange = (event) => {
    setAccount(event.target.value)
  }


  return (
    <div className="container">
        <h1>Hello my friend</h1>



        {/* <div>
            {currentAddress
                ? <>My current friend is <strong>{}</strong></>
                : <>I don't have any friend yet...</>
            }
        </div> */}

        <form onSubmit={handleSubmit}>
            <fieldset>
                <label>
                    <p>Enter the volunteer's name</p>
                    <input name="name" onChange={handleNameChange} />
                </label>
            </fieldset>

            <fieldset>
                <label>
                    <p>Enter the number of Hours</p>
                    <input name="Hours" onChange={handleHoursChange} />
                </label>
            </fieldset>

            <fieldset>
                <label>
                    <p>Enter the volunteer's address</p>
                    <input name="Address" onChange={handleAddressChange} />
                </label>
            </fieldset>


            <div className="mt-3">
                {name && <>Do you want to say hello to <strong>{name}</strong> ?</>}
                <div>
                    <button type="submit" className="btn btn-primary me-3">Say hello!</button>
                    <button type="button" className="btn btn-secondary me-3">Retrieve my friend</button>
                </div>
            </div>
        </form>
    </div>
  )
}
