import React, {useState, useEffect } from 'react';


const ValidateMission = ({ missions, onValidateMission }) => {

  const handleValidation = (mission) => {
    onValidateMission(mission);
  };

  return (
    <div className = "container">
        <div className = "h4"> Validate mamen </div>
        <div className = "h2"> Why you </div>

        <form onSubmit = {handleValidation} >

              <table>
              <thead>
                <tr>
                  <th>Mission Name</th>
                  <th>Hours</th>
                  <th>Volunteer Name</th>
                  <th>Volunteer Address</th>
                  <th></th>
                </tr>
              </thead>
              <tbody>
                {missions.map((mission, index) => (
                  <tr key={index}>
                    <td>{mission.name}</td>
                    <td>{mission.hours}</td>
                    <td>{mission.address}</td>
                    <td>{mission.id}</td>
                    <td>
                      <button onClick={() => handleValidation(mission)}>Validate Mission</button>
                    </td>
                  </tr>
                ))}
              </tbody>
            </table>

            <button type="submit" className="btn btn-primary" onClick={handleValidation}>Validate mission !</button>

        </form>
    </div>


    );
};

export default ValidateMission;
