import { EthProvider } from "./contexts/EthContext";
import ValidateMission from "./components/ValidateMission";
import GenerateToken from "./components/GenerateToken";
import "./App.css";
import { useEffect, useState } from 'react';
import web3 from 'web3';


import {
  BrowserRouter as Router,
  Switch,   
  Route,
  Link
} from "react-router-dom";
import Web3 from "web3";

function App() {

  const [account, setAccount] = useState('');
  const [admin, setAdmin] = useState('');
  const [volunteers, setVolunteers] = useState('');
  const [hours, setHours] = useState('');



  onSubmit = async (event) => {
    event.preventDefault();

    const accounts = await Web3.eth.getAccounts();

    this.setState({message : 'Waiting on transaction success...'}); 

    await eternitee.methods.enter().send({
    from: accounts[0],
    value : web3.utils.toWei(this.state.value, 'ether')
    })

    this.setState({message : 'mission Validated successfully !'}); 

  }

  return (


    <><><div>
      <form onSubmit={this.onSubmit}>
        <h4>Validate mission</h4>
        <label>Amount of Hours</label>
        <input>value = {this.state.value} onChange ={event => this.setState({ value: event.target.value })} </input>

        <label>Address of volunteer</label>
        <input>address = {this.state.address} onChange ={event => this.setState({ address: event.target.address })} </input>

        <button> Validate mission </button>
      </form>
    </div>

      <hr />

      </><h1>
        {this.state.message}
      </h1></>



  );
}

export default App;
